import styled from "styled-components";
import React from "react";
import ReactDOM from "react-dom";

const Sc_div = styled.div`
  width: 60%;
  margin: 10px auto;
  text-align: center;
`;

const Sc_img = styled.img`
  width: 200px;
  height: 200px;
`;

function Company(props) {
  const { company } = props;
  return (
    <div>
      <Sc_img src={company.img} />
      <h2>{company.name}</h2>
      <h3>{company.category}</h3>
    </div>
  );
}

export default Company;
