import styled from "styled-components";
import React from "react";
import ReactDOM from "react-dom";

const Sc_img = styled.img`
  width: 200px;
  height: 200px;
`;
const Sc_div = styled.div`
  width: 60%;
  margin: 10px auto;
  text-align: center;
  display: flex;
`;
const Sc_h2 = styled.h2`
  display: block;
`;
function Company(props) {
  const { company } = props;
  return (
    <Sc_div>
      <Sc_img src={company.img} />
      <h2>{company.name}</h2>
      <h3>{company.category}</h3>
      <p>
        {" "}
        {company.ratings} {company.deliveryTime}
      </p>
    </Sc_div>
  );
}

export default Company;
